import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function AddAppointment() {
  const [formData, setFormData] = useState({
    resident_id: '',
    date: '',
    time: '',
    reason: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://127.0.0.1:8000/appointments', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      if (!response.ok) {
        throw new Error('Erreur lors de l\'ajout du rendez-vous');
      }
      setSuccess('Rendez-vous ajouté avec succès !');
      setTimeout(() => navigate('/appointments'), 1500);
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="add-appointment-container">
      <h2>Ajouter un rendez-vous</h2>
      {error && <div className="alert error">{error}</div>}
      {success && <div className="alert success">{success}</div>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>ID du résident</label>
          <input type="number" name="resident_id" value={formData.resident_id} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Date</label>
          <input type="date" name="date" value={formData.date} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Heure</label>
          <input type="time" name="time" value={formData.time} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Motif</label>
          <input type="text" name="reason" value={formData.reason} onChange={handleChange} required />
        </div>
        <button type="submit">Ajouter</button>
      </form>
    </div>
  );
}

export default AddAppointment;
