import React, { useState, useEffect } from 'react';
import { XMarkIcon, PaperAirplaneIcon } from '@heroicons/react/24/outline';
import { useSelector } from 'react-redux';
import { Navigate } from 'react-router-dom';
import logo from '../assets/logo_isenior.png'; // Correction du nom du fichier
import '../styles/chat.css';

function Chat() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [error, setError] = useState('');
  const username = useSelector((state) => state.auth?.user?.username) || 'bintein_nicolas';
  const role = useSelector((state) => state.auth?.user?.role);

  useEffect(() => {
    if (role) {
      setMessages([{ text: `Bonjour ${username}, Comment puis-je t'aider?`, sent: false }]);
    }
  }, [role, username]);

  const sendMessageToChatGPT = async (message) => {
    try {
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer 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'
        },
        body: JSON.stringify({
          model: 'gpt-3.5-turbo',
          messages: [{ role: 'user', content: message }],
          temperature: 0.7
        })
      });
      if (!response.ok) throw new Error(`Erreur HTTP ${response.status}: ${await response.text()}`);
      const data = await response.json();
      return data.choices[0].message.content;
    } catch (err) {
      setError(`Erreur : ${err.message}`);
      return 'Désolé, je ne peux pas répondre pour le moment.';
    }
  };

  const handleSendMessage = async () => {
    if (inputMessage.trim() && role) {
      setMessages((prev) => [...prev, { text: inputMessage, sent: true }]);
      const reply = await sendMessageToChatGPT(inputMessage);
      setMessages((prev) => [...prev, { text: reply, sent: false }]);
      setInputMessage('');
    }
  };

  if (!role) return <Navigate to="/login" replace />;

  return (
    <div className="appointments-container">
      <img src={logo} alt="SeniorAI Logo" className="chat-logo" /> {/* Utilise le bon nom */}
      <div className="chat-messages">
        {messages.map((msg, index) => (
          <div key={index} className={msg.sent ? 'sent-message' : 'received-message'}>
            <span>{msg.text}</span>
          </div>
        ))}
      </div>
      <div className="form-group">
        <div className="message-input">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Tapez votre question..."
            onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
            className="prompt-input"
          />
          <span className="input-suffix">
            <PaperAirplaneIcon className="h-5 w-5" style={{ color: '#b0b0b0' }} onClick={handleSendMessage} />
          </span>
        </div>
      </div>
      {error && <div className="alert error">{error}</div>}
    </div>
  );
}

export default Chat;
