import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function EditAppointment() {
  const { id } = useParams();
  const [formData, setFormData] = useState({
    resident_id: '',
    date: '',
    time: '',
    reason: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAppointment = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:8000/appointments/${id}`, {
          method: 'GET',
          headers: { 'Content-Type': 'application/json' },
        });
        if (!response.ok) {
          throw new Error('Erreur lors de la récupération du rendez-vous');
        }
        const data = await response.json();
        setFormData({
          resident_id: data.resident_id,
          date: data.date,
          time: data.time,
          reason: data.reason
        });
      } catch (err) {
        setError(err.message);
      }
    };
    fetchAppointment();
  }, [id]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://127.0.0.1:8000/appointments/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      if (!response.ok) {
        throw new Error('Erreur lors de la modification du rendez-vous');
      }
      setSuccess('Rendez-vous modifié avec succès !');
      setTimeout(() => navigate(`/residents/${formData.resident_id}`), 1500);
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="edit-appointment-container">
      <h2>Modifier le rendez-vous</h2>
      {error && <div className="alert error">{error}</div>}
      {success && <div className="alert success">{success}</div>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>ID du résident</label>
          <input type="number" name="resident_id" value={formData.resident_id} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Date</label>
          <input type="date" name="date" value={formData.date} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Heure</label>
          <input type="time" name="time" value={formData.time} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Motif</label>
          <input type="text" name="reason" value={formData.reason} onChange={handleChange} required />
        </div>
        <button type="submit">Modifier</button>
      </form>
    </div>
  );
}

export default EditAppointment;
