import React, { useState, useEffect } from 'react';
import { XMarkIcon, PaperAirplaneIcon } from '@heroicons/react/24/outline';
import '../styles/views.css';

function Chat() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isChatOpen, setIsChatOpen] = useState(false);
  const [error, setError] = useState('');

  const sendMessageToChatGPT = async (message) => {
    try {
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer YOUR_OPENAI_API_KEY' // Remplace par ta clé API OpenAI
        },
        body: JSON.stringify({
          model: 'gpt-3.5-turbo',
          messages: [{ role: 'user', content: message }],
          temperature: 0.7
        })
      });
      if (!response.ok) throw new Error('Erreur avec l\'API ChatGPT');
      const data = await response.json();
      return data.choices[0].message.content;
    } catch (err) {
      setError('Erreur : ' + err.message);
      return 'Désolé, je ne peux pas répondre pour le moment.';
    }
  };

  const handleSendMessage = async () => {
    if (inputMessage.trim()) {
      setMessages((prev) => [...prev, { text: inputMessage, sent: true }]);
      const reply = await sendMessageToChatGPT(inputMessage);
      setMessages((prev) => [...prev, { text: reply, sent: false }]);
      setInputMessage('');
    }
  };

  return (
    <div className={`chat-container ${isChatOpen ? 'open' : ''}`}>
      <button onClick={() => setIsChatOpen(!isChatOpen)} className="chat-toggle">
        <span>Chat IA</span>
        <XMarkIcon className="h-5 w-5" />
      </button>
      {isChatOpen && (
        <div className="chat-box">
          <div className="messages">
            {messages.map((msg, index) => (
              <div key={index} className={msg.sent ? 'sent' : 'received'}>
                {msg.text}
              </div>
            ))}
          </div>
          <div className="message-input">
            <input
              type="text"
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              placeholder="Posez une question à l'IA..."
              onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
            />
            <button onClick={handleSendMessage} className="send-button">
              <PaperAirplaneIcon className="h-5 w-5" />
            </button>
          </div>
          {error && <div className="alert error">{error}</div>}
        </div>
      )}
    </div>
  );
}

export default Chat;
